mapper.insert:
	/**
 	* Query ID : insertAgentInfo
 	* Description : Agent 설치 등록
 	* Author : 박태혁
 	**/
 	INSERT INTO T_AGTCNDT_I
 	(
		DB_SVR_ID
		, SOCKET_PORT
		, AGT_CNDT_CD
		, STRT_DTM
		, ISTCNF_YN
		, FRST_REGR_ID
		, FRST_REG_DTM
		, LST_MDFR_ID
		, LST_MDF_DTM
 	)
 	VALUES
 	(
		#{DB_SVR_ID}
		, #{SOCKET_PORT}
		, #{AGT_CNDT_CD}
		, now()
		, #{ISTCNF_YN}
		, #{FRST_REGR_ID}
		, now()
		, #{LST_MDFR_ID}
		, now()
 	)

mapper.update:

	/**
 	* Query ID : updateAgentInfo
 	* Description : Agent 설치 정보 수정
 	* Author : 박태혁
 	**/
 	UPDATE T_AGTCNDT_I
 	SET 
 		AGT_CNDT_CD = #{AGT_CNDT_CD}
 		, SOCKET_PORT = #{SOCKET_PORT}
 		, ISTCNF_YN = #{ISTCNF_YN}
 		, LST_MDF_DTM = now()
 	WHERE
 		DB_SVR_ID = #{DB_SVR_ID}
