mapper.insert:
	/**
 	* Query ID : insertAgentInfo
 	* Description : Agent 설치 등록
 	* Author : 박태혁
 	**/
 	INSERT INTO T_AGTCNDT_I
 	(
		AGT_SN
		, IPADR
		, SOCKET_PORT
		, AGT_CNDT_CD
		, AGT_VERSION
		, STRT_DTM
		, ISTCNF_YN
		, FRST_REGR_ID
		, FRST_REG_DTM
		, LST_MDFR_ID
		, LST_MDF_DTM
 	)
 	VALUES
 	(
		NEXTVAL('Q_AGTCNDT_I_01')
		, #{IPADR}
		, #{SOCKET_PORT}
		, #{AGT_CNDT_CD}
		, #{AGT_VERSION}
		, now()
		, #{ISTCNF_YN}
		, #{FRST_REGR_ID}
		, now()
		, #{LST_MDFR_ID}
		, now()
 	)

mapper.update:

	/**
 	* Query ID : updateAgentInfo
 	* Description : Agent 설치 정보 수정
 	* Author : 박태혁
 	**/
 	UPDATE T_AGTCNDT_I
 	SET 
 		AGT_CNDT_CD = #{AGT_CNDT_CD}
 		, AGT_VERSION = #{AGT_VERSION}
 		, SOCKET_PORT = #{SOCKET_PORT}
 		, ISTCNF_YN = #{ISTCNF_YN}
 		, LST_MDF_DTM = now()
 		, STRT_DTM = now()
 	WHERE
 		IPADR = #{IPADR}

   		/**
	 	* Query ID : updateT_WRKEXE_G
	 	* Description : 작업실행로그 update
	 	* Author : 박태혁
	 	**/
		UPDATE T_WRKEXE_G
		SET
			WRK_END_DTM = now()
			, EXE_RSLT_CD = #{EXE_RSLT_CD}
			, FILE_SZ = #{FILE_SZ}
			, BCK_FILENM = #{BCK_FILENM}
			, RSLT_MSG = #{RSLT_MSG}
		WHERE
			EXE_SN = #{EXE_SN}

   		/**
	 	* Query ID : updateT_TRFTRGCNG_I
	 	* Description : BottledWater 실행 PID update
	 	* Author : 박태혁
	 	**/
	 	UPDATE T_TRFTRGCNG_I
	 	SET
	 		BW_PID = #{BW_PID}
	 	WHERE
	 		TRF_TRG_ID = #{TRF_TRG_ID}
