mapper.select:
/** * Query ID : selectAccessHistory * Description : 접근내역 조회 * Author : 김주영 **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM , (ROW_NUMBER() OVER()) AS IDX , A.EXEDTM_DATE , A.EXEDTM_HOUR , A.SYS_CD_NM , A.USR_ID , A.USR_NM , A.DEPT_NM , A.PST_NM , A.LGI_IPADR FROM( SELECT TO_CHAR(G.EXEDTM,'YYYY-MM-DD')EXEDTM_DATE , TO_CHAR(G.EXEDTM,'HH24:MI')EXEDTM_HOUR , C.SYS_CD_NM , G.USR_ID , I.USR_NM , I.DEPT_NM , I.PST_NM , G.LGI_IPADR FROM T_SYSWRK_G G, T_SYSDTL_C C, T_USR_I I WHERE G.EXE_DTL_CD =C.SYS_CD AND G.USR_ID=I.USR_ID
AND to_char(G.EXEDTM,'YYYY-MM-DD') between #{lgi_dtm_start} and #{lgi_dtm_end}
AND I.USR_NM LIKE #{usr_nm}
ORDER BY EXEDTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex} )A

/** * Query ID : selectAccessHistoryTotCnt * Description : selectAccessHistoryTotCnt TotCnt 조회 * Author : 김주영 **/ SELECT COUNT(*) totcnt FROM T_SYSWRK_G G, T_SYSDTL_C C, T_USR_I I WHERE G.EXE_DTL_CD =C.SYS_CD AND G.USR_ID=I.USR_ID
AND to_char(G.EXEDTM,'YYYY-MM-DD') between #{lgi_dtm_start} and #{lgi_dtm_end}
AND I.USR_NM LIKE #{usr_nm}

/** * Query ID : selectAccessHistoryExcel * Description : 엑셀 접근내역 조회 * Author : 김주영 **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM , (ROW_NUMBER() OVER()) AS IDX , A.EXEDTM_DATE , A.EXEDTM_HOUR , A.SYS_CD_NM , A.USR_ID , A.USR_NM , A.DEPT_NM , A.PST_NM , A.LGI_IPADR FROM( SELECT TO_CHAR(G.EXEDTM,'YYYY-MM-DD')EXEDTM_DATE , TO_CHAR(G.EXEDTM,'HH24:MI')EXEDTM_HOUR , C.SYS_CD_NM , G.USR_ID , I.USR_NM , I.DEPT_NM , I.PST_NM , G.LGI_IPADR FROM T_SYSWRK_G G, T_SYSDTL_C C, T_USR_I I WHERE G.EXE_DTL_CD =C.SYS_CD AND G.USR_ID=I.USR_ID
AND to_char(G.EXEDTM,'YYYY-MM-DD') between #{lgi_dtm_start} and #{lgi_dtm_end}
AND I.USR_NM LIKE #{usr_nm}
ORDER BY EXEDTM DESC )A
