mapper.insert:
    	/**
	 	*  Query ID : insertDbServer
	 	* Description : insertDbServer 등록
	 	* Author : 변승우
	 	**/
	 	
		INSERT INTO T_DBSVR_I
		(
			  DB_SVR_ID
			, DB_SVR_NM
			, IPADR
			, PORTNO
			, DFT_DB_NM
			, SVR_SPR_USR_ID
			, SVR_SPR_SCM_PWD
			, ISTPATH
			, FRST_REGR_ID
			, FRST_REG_DTM
			, LST_MDFR_ID
			, LST_MDF_DTM
		) 
		VALUES
		(
			  nextval('q_dbsvr_i_01')
			, #{db_svr_nm}
			, #{ipadr}
			, #{portno}
			, #{dft_db_nm}
			, #{svr_spr_usr_id}
			, #{svr_spr_scm_pwd}
			, #{istpath}
			, #{frst_regr_id}
			, clock_timestamp()
			, #{lst_mdfr_id}
			, clock_timestamp()	
		)

mapper.update:
   	    /**
	 	* Query ID : updateDbServer
	 	* Description : DB서버 수정
	 	* Author : 변승우
	 	**/
	 	
		UPDATE T_DBSVR_I
		SET 
	  	 	   DB_SVR_NM= #{db_svr_nm}
		      , IPADR=#{ipadr}
		      , PORTNO=#{portno}
		      , DFT_DB_NM=#{dft_db_nm}
			  , SVR_SPR_USR_ID=#{svr_spr_usr_id}
			  , SVR_SPR_SCM_PWD=#{svr_spr_scm_pwd}
			  , ISTPATH=#{istpath}
			  , LST_MDFR_ID=#{lst_mdfr_id}
			  , LST_MDF_DTM=clock_timestamp()	
		WHERE DB_SVR_ID=#{db_svr_id}

mapper.select:
/** * Query ID : selectSvrList * Description : Repository 등록된 DB의 서버 리스트 조회 * Author : 변승우 **/
SELECT A.DB_SVR_ID , A.DB_SVR_NM , A.IPADR , A.PORTNO FROM T_DBSVR_I A, T_DB_I B WHERE 1 = 1 AND A.DB_SVR_ID = B.DB_SVR_ID
AND A.DB_SVR_ID = #{value}
GROUP BY A.DB_SVR_NM, A.DB_SVR_ID, A.IPADR, A.PORTNO
