mapper.select:
/** * Query ID : selectWorkList * Description : workList 조회 * Author : Yoon jh **/
SELECT WRK_ID, DB_SVR_ID, DB_ID, (CASE WHEN DB_ID>0 THEN (SELECT DB_NM FROM T_DB_I WHERE A.DB_ID=DB_ID AND USEYN = 'Y') ELSE '' END) AS DB_NM, BCK_BSN_DSCD, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.BCK_BSN_DSCD = SYS_CD ) AS BCK_BSN_DSCD_NM, WRK_NM, WRK_EXP, BCK_OPT_CD, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.BCK_OPT_CD = SYS_CD ) AS BCK_OPT_CD_NM, BCK_MTN_ECNT, CPS_YN, LOG_FILE_BCK_YN, LOG_FILE_STG_DCNT, LOG_FILE_MTN_ECNT, CPRT, SAVE_PTH, FILE_FMT_CD, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.FILE_FMT_CD = SYS_CD ) AS FILE_FMT_CD_NM, FILE_STG_DCNT, ENCD_MTH_NM, USR_ROLE_NM, FRST_REGR_ID, TO_CHAR(FRST_REG_DTM,'YYYY-MM-DD HH24:MI') AS FRST_REG_DTM, LST_MDFR_ID, TO_CHAR(LST_MDF_DTM,'YYYY-MM-DD HH24:MI') AS LST_MDF_DTM, BCK_FILENM, DATA_PTH, BCK_PTH, ACV_FILE_STGDT, ACV_FILE_MTNCNT FROM T_WRKCNG_I A WHERE 1=1 AND DB_SVR_ID=#{db_svr_id} AND BCK_BSN_DSCD=#{bck_bsn_dscd}
AND WRK_NM LIKE CONCAT('%',#{wrk_nm},'%')
AND BCK_OPT_CD=#{bck_opt_cd}
AND WRK_ID=#{wrk_id}
AND DB_ID=#{db_id}
ORDER BY WRK_ID DESC

mapper.delete:
	/**
 	* Query ID : deleteWorkOpt
 	* Description : 백업옵션 delete
 	* Author : Yoon jh
 	**/	
	DELETE FROM t_bckopt_i
	WHERE
		WRK_ID = #{wrk_id}
		
			
		AND OPT_SN = #{opt_sn}

mapper.select:
/** * Query ID : selectWorkLogList * Description : 백업로그 List 조회 * Author : Yoon jh **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM, TWG.EXE_SN AS EXE_SN, TWG.SCD_ID AS SCD_ID, (SELECT SCD_NM FROM T_SCD_M WHERE TWG.SCD_ID = SCD_ID ) AS SCD_NM, TWG.WRK_ID AS WRK_ID, TWI.WRK_NM, to_char(TWG.WRK_STRT_DTM,'YYYY-MM-DD HH24:MI') AS WRK_STRT_DTM, to_char(TWG.WRK_END_DTM,'YYYY-MM-DD HH24:MI') AS WRK_END_DTM, TWG.EXE_RSLT_CD AS EXE_RSLT_CD, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE TWG.EXE_RSLT_CD = SYS_CD ) AS EXE_RSLT_CD_NM, TWG.BCK_OPT_CD AS BCK_OPT_CD, (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE TWG.BCK_OPT_CD = SYS_CD ) AS BCK_OPT_CD_NM, TWG.TLI AS TLI, TWG.FILE_SZ AS FILE_SZ, TWG.DB_ID AS DB_ID, (SELECT DB_NM FROM T_DB_I WHERE TWG.DB_ID=DB_ID AND USEYN = 'Y') AS DB_NM, TWG.BCK_FILE_PTH AS BCK_FILE_PTH, TWG.FRST_REGR_ID AS FRST_REGR_ID, to_char(TWG.FRST_REG_DTM,'YYYY-MM-DD HH24:MI') AS FRST_REG_DTM, TWG.LST_MDFR_ID AS LST_MDFR_ID, to_char(TWG.LST_MDF_DTM,'YYYY-MM-DD HH24:MI') AS LST_MDF_DTM FROM T_WRKEXE_G TWG, T_WRKCNG_I TWI WHERE 1=1 AND TWG.WRK_ID = TWI.WRK_ID
AND TWG.WRK_ID=#{wrk_id}
AND TWG.SCD_ID=#{scd_id}
AND TWG.EXE_SN=#{exe_sn}
AND TWG.DB_ID=#{db_id}
AND TWI.BCK_BSN_DSCD = #{bck_bsn_dscd}
AND TWG.BCK_OPT_CD = #{bck_opt_cd}
AND ( TO_DATE(to_char(TWG.WRK_STRT_DTM,'YYYY-MM-DD'),'YYYY-MM-DD') BETWEEN TO_DATE(#{wrk_strt_dtm},'YYYY-MM-DD') AND TO_DATE(#{wrk_end_dtm},'YYYY-MM-DD') OR TO_DATE(to_char(TWG.WRK_END_DTM,'YYYY-MM-DD'),'YYYY-MM-DD') BETWEEN TO_DATE(#{wrk_strt_dtm},'YYYY-MM-DD') AND TO_DATE(#{wrk_end_dtm},'YYYY-MM-DD') )
AND TWG.EXE_RSLT_CD=#{exe_rslt_cd}
ORDER BY TWG.EXE_SN DESC
