mapper.select:
/** * Query ID : selectWorkList * Description : 스케쥴 work List 조회 * Author : 변승우 **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM , (ROW_NUMBER() OVER()) AS IDX , WRK_ID , DB_SVR_ID , (SELECT DB_SVR_NM FROM T_DBSVR_I WHERE A.DB_SVR_ID = DB_SVR_ID ) AS DB_SVR_NM , DB_ID , (CASE WHEN DB_ID>0 THEN (SELECT DB_NM FROM T_DB_I WHERE A.DB_ID=DB_ID ) ELSE '' END) AS DB_NM , BCK_BSN_DSCD , (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.BCK_BSN_DSCD = SYS_CD ) AS BCK_BSN_DSCD_NM , WRK_NM , WRK_EXP , BCK_OPT_CD , (SELECT SYS_CD_NM FROM T_SYSDTL_C WHERE A.BCK_OPT_CD = SYS_CD ) AS BCK_OPT_CD_NM , BCK_MTN_ECNT , CPS_YN , LOG_FILE_BCK_YN , LOG_FILE_STG_DCNT , LOG_FILE_MTN_ECNT , CPRT , SAVE_PTH , FILE_FMT_CD , FILE_STG_DCNT , ENCD_MTH_NM , USR_ROLE_NM , FRST_REGR_ID , TO_CHAR(FRST_REG_DTM,'YYYY-MM-DD HH24:MI:SS') AS FRST_REG_DTM , LST_MDFR_ID , TO_CHAR(LST_MDF_DTM,'YYYY-MM-DD HH24:MI:SS') AS LST_MDF_DTM FROM T_WRKCNG_I A WHERE 1=1 AND BCK_BSN_DSCD=#{bck_bsn_dscd}
AND DB_SVR_ID IN(SELECT DB_SVR_ID FROM T_DBSVR_I WHERE DB_SVR_NM = #{db_svr_nm})
AND WRK_NM LIKE CONCAT('%',#{wrk_nm},'%')
