mapper.insert:
/**
* Query ID : insertTransferSetting
* Description : 전송설정 등록
* Author : 김주영
**/
INSERT INTO T_TRFCNG_I(
  TRF_CNG_ID
, KAFKA_BROKER_IP
, KAFKA_BROKER_PORT
, SCHEMA_REGISTRY_IP
, SCHEMA_REGISTRY_PORT
, ZOOKEEPER_IP
, ZOOKEEPER_PORT
, BW_HOME
, FRST_REGR_ID
, FRST_REG_DTM
, LST_MDFR_ID
, LST_MDF_DTM
) 
VALUES(         
  nextval('q_trfcng_i_01')
, #{kafka_broker_ip}
, #{kafka_broker_port}
, #{schema_registry_ip}
, #{schema_registry_port}
, #{zookeeper_ip}
, #{zookeeper_port}
, #{bw_home}	
, #{frst_regr_id}
, clock_timestamp()
, #{lst_mdfr_id}
, clock_timestamp()
)

mapper.update:
/**
* Query ID : updateTransferSetting
* Description : 전송설정 업데이트
* Author : 김주영
**/	
UPDATE T_TRFCNG_I
SET
  KAFKA_BROKER_IP = #{kafka_broker_ip}
, KAFKA_BROKER_PORT = #{kafka_broker_port}
, SCHEMA_REGISTRY_IP = #{schema_registry_ip}
, SCHEMA_REGISTRY_PORT = #{schema_registry_port}
, ZOOKEEPER_IP = #{zookeeper_ip}
, ZOOKEEPER_PORT = #{zookeeper_port}
, BW_HOME = #{bw_home}
, LST_MDFR_ID = #{lst_mdfr_id}
, LST_MDF_DTM = clock_timestamp()
WHERE TRF_CNG_ID = #{trf_cng_id}

mapper.select:
/** * Query ID : selectConnectorRegister * Description : Connector 조회 * Author : 김주영 **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM , (ROW_NUMBER() OVER()) AS IDX , A.CNR_ID , A.CNR_NM , A.CNR_IPADR , A.CNR_PORTNO , A.CNR_CNN_TP_CD , A.FRST_REGR_ID , A.FRST_REG_DTM , A.LST_MDFR_ID , A.LST_MDF_DTM FROM( SELECT CNR_ID , CNR_NM , CNR_IPADR , CNR_PORTNO , CNR_CNN_TP_CD , FRST_REGR_ID , to_char(T_CNR_I.FRST_REG_DTM,'YYYY-MM-DD HH:mm:ss')FRST_REG_DTM , LST_MDFR_ID , to_char(T_CNR_I.LST_MDF_DTM,'YYYY-MM-DD HH:mm:ss')LST_MDF_DTM FROM T_CNR_I WHERE 1=1
AND CNR_NM LIKE #{cnr_nm}
AND CNR_IPADR LIKE #{cnr_ipadr}
ORDER BY LST_MDF_DTM DESC )A

