mapper.update:
		/**
		* Query ID : updateMypage
		* Description : 개인정보 수정
		* Author : 김주영
		**/
 		UPDATE T_USR_I
		SET 
		  USR_NM=#{usr_nm}
		, BLN_NM=#{bln_nm}
		, DEPT_NM=#{dept_nm}
		, RSP_BSN_NM=#{rsp_bsn_nm}
		, PST_NM=#{pst_nm}
		, CPN=#{cpn}
		, LST_MDFR_ID=#{lst_mdfr_id}
		, LST_MDF_DTM= clock_timestamp()
		WHERE usr_id = #{usr_id}
