mapper.insert:
/**
* Query ID : insertTransferTarget
* Description : 전송대상 등록
* Author : 김주영
**/
INSERT INTO T_TRFTRGCNG_I
(
  TRF_TRG_ID
, TRF_TRG_CNN_NM
, TRF_TRG_URL
, CONNECTOR_CLASS
, TASK_MAX
, HADOOP_CONF_DIR
, HADOOP_HOME
, FLUSH_SIZE
, ROTATE_INTERVAL_MS
, CNR_ID
, FRST_REGR_ID
, FRST_REG_DTM
, LST_MDFR_ID
, LST_MDF_DTM
)
VALUES
(
  nextval('q_trftrgcng_i_01')
, #{trf_trg_cnn_nm}
, #{trf_trg_url}
, #{connector_class}
, #{task_max}
, #{hadoop_conf_dir}
, #{hadoop_home}
, #{flush_size}
, #{rotate_interval_ms}
, #{cnr_id}
, #{frst_regr_id}
, clock_timestamp()
, #{lst_mdfr_id}
, clock_timestamp()
)

mapper.update:
/**
* Query ID : updateTransferTarget
* Description : 전송대상 업데이트
* Author : 김주영
**/	
UPDATE T_TRFTRGCNG_I
SET
  TRF_TRG_URL=#{trf_trg_url}
, CONNECTOR_CLASS=#{connector_class}
, TASK_MAX=#{task_max}
, HADOOP_CONF_DIR=#{hadoop_conf_dir}
, HADOOP_HOME=#{hadoop_home}
, FLUSH_SIZE=#{flush_size}
, ROTATE_INTERVAL_MS=#{rotate_interval_ms}
, LST_MDFR_ID=#{lst_mdfr_id}
, LST_MDF_DTM= clock_timestamp()
WHERE TRF_TRG_CNN_NM=#{trf_trg_cnn_nm}

