mapper.select:
/** * Query ID : selectProxyServerList * Description : proxy server list * Author : 윤정 **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM, (ROW_NUMBER() OVER()) AS IDX, s.PRY_SVR_ID, s.IPADR, s.AGT_SN, s.PRY_SVR_NM, s.PRY_PTH, s.KAL_PTH, s.USE_YN, s.EXE_STATUS, s.MASTER_GBN, s.MASTER_SVR_ID, s.DB_SVR_ID, s.DAY_DATA_DEL_TERM, s.MIN_DATA_DEL_TERM, s.FRST_REGR_ID, s.FRST_REG_DTM, s.LST_MDFR_ID, s.LST_MDF_DTM, a.AGT_CNDT_CD FROM T_PRY_SVR_I s join T_PRY_AGT_I a on s.AGT_SN = a.AGT_SN
WHERE PRY_SVR_ID = #{value} OR MASTER_SVR_ID = #{value}
ORDER BY PRY_SVR_ID, LST_MDF_DTM

/** * Query ID : selectProxyServerByMasterId * Description : proxy server cluster 1set * Author : 윤정 **/ SELECT (ROW_NUMBER() OVER()) AS ROWNUM, (ROW_NUMBER() OVER()) AS IDX, s.PRY_SVR_ID, s.IPADR, s.AGT_SN, s.PRY_SVR_NM, s.PRY_PTH, s.KAL_PTH, s.USE_YN, s.EXE_STATUS, s.MASTER_GBN, s.MASTER_SVR_ID, s.DB_SVR_ID, s.DAY_DATA_DEL_TERM, s.MIN_DATA_DEL_TERM, s.FRST_REGR_ID, s.FRST_REG_DTM, s.LST_MDFR_ID, s.LST_MDF_DTM, a.AGT_CNDT_CD, v.V_IP FROM T_PRY_SVR_I s JOIN T_PRY_AGT_I a ON s.AGT_SN = a.AGT_SN JOIN T_PRY_VIPCNG_I v ON s.PRY_SVR_ID = v.PRY_SVR_ID
WHERE (s.PRY_SVR_ID = #{value} OR s.MASTER_SVR_ID = #{value})
AND v.STATE_NM = 'MASTER' ORDER BY s.PRY_SVR_ID, LST_MDF_DTM
