mapper.insert:
/**
* Query ID : insertScaleLog
* Description : scale로그 등록
* Author : 
**/	
INSERT INTO t_scale_g
(
	scale_wrk_sn
	, wrk_id
	, scale_gbn
	, wrk_strt_dtm
	, wrk_end_dtm
	, exe_rslt_cd
	, rslt_msg
	, instance_nm
	, instance_id
	, frst_regr_id
	, frst_reg_dtm
	, lst_mdfr_id
	, lst_mdf_dtm
)
VALUES
(
  nextval('q_t_scale_g_01')
, #{wrk_id}::numeric
, #{scale_gbn}
, #{wrk_strt_dtm}	
, #{wrk_end_dtm}
, #{exe_rslt_cd}
, #{rslt_msg}
, #{instance_nm}
, #{instance_id}
, #{frst_regr_id}
, clock_timestamp()
, #{lst_mdfr_id}
, clock_timestamp()
)

mapper.select:
/** * Query ID : selectScaleLog * Description : SCALE Load 조회 * Author : **/ SELECT A.WRK_ID , A.FRST_REGR_ID , A.EXE_RSLT_CD , A.SCALE_GBN FROM T_SCALE_G A WHERE 1 = 1
AND A.SCALE_WRK_SN = (SELECT SCALE_WRK_SN FROM T_SCALE_G WHERE 1= 1 AND INSTANCE_ID = #{instance_id} AND SCALE_GBN IN ('1', '2') )
AND A.SCALE_WRK_SN = (SELECT MAX(SCALE_WRK_SN) FROM T_SCALE_G WHERE EXE_RSLT_CD = 'TC001701' AND SCALE_GBN IN ('1', '2') )
